package systemobject.aqua.automation.utils.utils.numeric;import java.util.Random;/** * Generic Class, can be used outside JSystem Project *  * @author Uri.Koaz */public class RandomUtils {	public static int[] getRandomArray(int[] arr) {		Random rand = new Random();		boolean[] boolArr = new boolean[arr.length];		int[] newArray = new int[arr.length];		for (int i = 0; i < arr.length; i++) {			int randValue = rand.nextInt(arr.length);			while (boolArr[randValue]) {				randValue = rand.nextInt(arr.length);			}			boolArr[randValue] = true;			newArray[randValue] = arr[i];		}		return newArray;	}	public static void main(String[] args) {		int[] rnadArray = getRandomArray(new int[] { 1, 2, 3, 4, 5 });		for (int i = 0; i < rnadArray.length; i++) {			System.out.println(rnadArray[i]);		}		System.out.println("\n");		rnadArray = getRandomArray(new int[] { 1, 2, 3, 4, 5 });		for (int i = 0; i < rnadArray.length; i++) {			System.out.println(rnadArray[i]);		}		System.out.println("\n");		rnadArray = getRandomArray(new int[] { 1, 2, 3, 4, 5 });		for (int i = 0; i < rnadArray.length; i++) {			System.out.println(rnadArray[i]);		}	}}